INSERT INTO `meituan_xinglong` VALUES ('1728', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '验票后有讲解员领入园区，每人身上贴个粘贴，凑成一个临时团，每到一个景点就讲解一番，全程将近一小时，验，整体上感觉不错！', '2016-01-28 21:54:09', '', 'None');
