INSERT INTO `meituan_xinglong` VALUES ('1757', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '公园很美景色宜人，很多热带植物是我们北方人从没见过的，很多只听说没见过的物种在这里都能见到，来这里很长知识。免费导游服务很好，还帮助游客拍照，赞一个。免费茶很好喝，咖啡很香。对于北方的游客自己游玩会很逊色。', '2015-12-24 06:34:32', '-1934010069:http://p1.meituan.net/shaitu/65e0080fc8df4ce7a8620e4e4e9097b9323139.jpg;\n-1934010053:http://p0.meituan.net/shaitu/3592f668f5a423dabf24865ba45c7bfc339798.jpg;\n-1934598366:http://p0.meituan.net/shaitu/0f4d6e44f86d6b0f2b2459302beb19f7322711.jpg;\n', 'None');
