INSERT INTO `meituan_yanuoda` VALUES ('961', '邻家姐姐·', '314516596', 'https://img.meituan.net/avatar/6277bd9bf2f7e210aef67bd5235979e9108854.jpg', '去呀喏哒玩了一天 自己去的 里面的人都超级热情 因为买的门票之后 一个小姐姐推荐走水路 果断答应 过程还是很开心的 水路第一关怕湿就没玩 结果最后一关还是不能幸免 哈哈哈哈 走钢丝什么的还可以 就是有一点累 景区里的菠萝蜜超级好吃 鲤鱼也好多 还有天鹅 水路走完又去别的地方溜达溜达 一样的好看 至于价格么 我觉得有一点不合理 没有直接分开陆路和水路 想走水路的小伙伴们还要多花150块钱 （但是过程还是很有意思的） 至于像有的人说什么没意思不好看之类的 我觉得分人吧 我就觉得去一趟呀喏哒挺开心的 毕竟出来玩 还有要善于发现美哦 希望小可爱们能有一个愉快的游玩体验哦 （记得自己带吃的 我发现景区里的吃的是真心不好吃 ） ?', '2018-03-08 17:59:01', '-1486260549:http://p0.meituan.net/shaitu/b657a9e9c976d12313b23d4f9826d482349291.jpg;\n-1486260550:http://p0.meituan.net/shaitu/5fd0194dcbc68e98d246b30b8c989432228861.jpg;\n-1486161307:http://p0.meituan.net/shaitu/9cbc03d5b97991a8b7e23b08c60c492b315908.jpg;\n-1486161306:http://p0.meituan.net/shaitu/47f8e8bfdd212d523ed177255f6a522a322958.jpg;\n-1486161305:http://p0.meituan.net/shaitu/95cea8d193cff86e74444ce8c94b6ea3304057.jpg;\n', 'None');
