INSERT INTO `meituan_yanuoda` VALUES ('1541', '喵喵淼妙222', '521751619', 'https://img.meituan.net/avatar/3bbc81a9b52e734283531615019803ae26546.jpg', '景区一般，就是热带植物，被人忽悠定了个七百的观光车，结果屁用没有，所有的景点都在山上山下，都要自己爬，观光车就走了一站地那么远，老人小孩不建议去，建议去槟榔谷，腿脚好的可以考虑，最好自己带点吃的喝的，里边比较大，带孩子老人，去猴岛也不错，在三亚自驾十多天，觉得猴岛和槟榔谷和比较适合孩子老人', '2017-12-23 21:30:44', '-1841792259:http://p1.meituan.net/shaitu/c7bdcc97561c9100c47c40afac6a9057297223.jpg;\n-1841893499:http://p1.meituan.net/shaitu/2620c80692380d3b22ccad65f64ce9e0312066.jpg;\n-1841893498:http://p1.meituan.net/shaitu/89d274b4c20fb8e925144356097d2e2c175783.jpg;\n-1841893497:http://p1.meituan.net/shaitu/d319e7083beaf8f785d134651ee4be4c439217.jpg;\n-1841893496:http://p1.meituan.net/shaitu/85411cbf345c634f86453790382bdf87450737.jpg;\n-1841792258:http://p0.meituan.net/shaitu/4201620a7f930cc69fce81f456a0dd5b499380.jpg;\n-1841792260:http://p0.meituan.net/shaitu/572aaf3d5f6e9f10b5260ba608e645aa547260.jpg;\n-1841893500:http://p1.meituan.net/shaitu/b0888af54b880f2960508c7f73edb104403571.jpg;\n', 'None');
