INSERT INTO `meituan_yanuoda` VALUES ('4369', '简单的生活770106', '67469328', 'https://img.meituan.net/avatar/37e64971c008c5ada155588f3484667210237.jpg', '生态环境好，注重人文关怀，服务人员热情礼貌，对老年人有优先照顾！环境干净整洁，安排合理，看了美景，剩下的一颗星，待我两年后，把山逛全了，把游乐项目玩一遍，再补上，留点遗憾吧！期待下次再来！和吴支洲一样，玩就有意思，只看还是索然了一些！年轻人可挑战一下！腿脚好的多遛遛，注意鞋，裤装，防蚊！', '2017-02-05 00:02:46', '-1896787929:http://p0.meituan.net/shaitu/b06325dda4d549b4ab5fa7c81b4565cb536091.jpg;\n-1897297862:http://p0.meituan.net/shaitu/56787c3cdb9525f6757cccb9fee20bc6442561.jpg;\n-1897161074:http://p0.meituan.net/shaitu/1f10cf2f8f89fcbb29e713c30a9dd9dc328778.jpg;\n-1896761247:http://p0.meituan.net/shaitu/ce36adbd769bd3d1ae64c3b34910ed2d512673.jpg;\n-1896681003:http://p0.meituan.net/shaitu/a417d73fadaf9276a1bc90e44bbd7898342729.jpg;\n-1896792231:http://p0.meituan.net/shaitu/a3388b443521593f7d2397807fb2cc3b746795.jpg;\n', 'None');
