INSERT INTO `meituan_yanuoda` VALUES ('4418', 'IRj828826982', '319035161', 'https://img.meituan.net/avatar/61b560e3340be9f0cd82d4361629d978307132.jpg', '呀诺达雨林文化旅游区位于三亚市北偏东方向的保亭黎族自治县三道农场，距离三亚市中心35公里。是中国唯一地处北纬18度的热带雨林，是海南岛五大热带雨林精品的浓缩，是最具观赏价值的热带雨林资源博览馆，堪称中国钻石级雨林景区。北连五指山、七仙岭和中部绿色腹地，在景区的观景台可眺望远景，海棠湾岸线、蜈支洲岛、猴岛尽收眼底的景观冲击，形成了与众不同的区位优势。 我们渡假就住在呀诺达附近，特别方便，不过我建议最好保存好全身体力再去，我来了两次都只是溜达了一半的路程，景点和自然风光映入眼底，美不胜收！来的亲们一定要穿舒服的鞋子哦！', '2017-02-04 08:51:53', '-1897486542:http://p0.meituan.net/shaitu/656602ca758d80eba4219af0cbdfcd55401722.jpg;\n-1896805211:http://p0.meituan.net/shaitu/915b84d5d8afddf938c21931aa459d55549359.jpg;\n-1896614872:http://p0.meituan.net/shaitu/15022dd03f5da7a34bbf6ef1a2fce1dc667535.jpg;\n', 'None');
