INSERT INTO `meituan_yanuoda` VALUES ('5022', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '第一次来到热带地区，很多植物都没有见过。有霸气的过江龙，有见血封喉的树，有世界上最大的草-旅人蕉，有在黎族象征阿哥的椰子树和阿妹的槟榔树，还有香蕉树，木瓜树什么的。值！', '2017-01-17 13:15:23', '-1898703431:http://p1.meituan.net/shaitu/55fbfbc97be64e3425891b3e17ca94ee22196.jpg;\n-1898545088:http://p1.meituan.net/shaitu/5b99105ade2c38e47842fcaf0f4be82a101461.jpg;\n', 'None');
