INSERT INTO `meituan_yanuoda` VALUES ('6329', 'sarazx789', '145006563', 'https://img.meituan.net/avatar/e835c50483425a47aa77f9cda7860ce764437.jpg', '来三亚亚龙湾都没有去 先来的呀诺达 毕竟是是国内唯一 \n首先 服务态度特别好  我们走的水路 教练很辛苦但还是努力调节气氛 在水路不觉得热 8中项目 涉及不同等级难度  除了手机其他的都不用带 妨碍过关 另外记得带毛巾跟换洗的内衣裤 一定会湿身 还要带手机防水袋\n其次 设施比较完善 想走也可以 不想走也有车\n最后 景色真的不错 山间美景 大氧吧 回味无穷\n', '2016-08-07 17:26:57', '', 'None');
