INSERT INTO `meituan_yanuoda` VALUES ('6930', 'NVj18630543399', '37458472', '', '适合早点去，非常美的地方。可以逛多半天，带孩子可以去了解好多植被，看自然景观要比人造景观美多了，还可以在里面品尝当地的小吃。坐索道，在小亭子里休息一下，看太阳落山，不过大家一定要记得涂抹防蚊药水，里面的蚊虫很厉害的。', '2016-03-25 19:33:45', '-1936110848:http://p0.meituan.net/shaitu/5df230faa0f7290edc1165baa55738c6539292.jpg;\n-1936173097:http://p1.meituan.net/shaitu/2fd09cfae6fde7aa78de505182500414554687.jpg;\n-1936049060:http://p0.meituan.net/shaitu/553e625fffbe7e295804c443eba9891e457641.jpg;\n', 'None');
