INSERT INTO `meituan_yanuoda` VALUES ('6948', 'RGy364890912', '109471369', '', '呀诺达热带雨林真的是很美很美，是三亚最值得去的地方。像极了人间仙境，瀑布很美，海中岛也很漂亮，真心推荐，景点非常多，团购价格比现场便宜很多，很超值。', '2016-03-21 11:59:09', '', 'None');
