INSERT INTO `meituan_yanuoda` VALUES ('7034', '_qqs4v1340872439', '28791701', '', '热带雨林就是风景宜人，场内还有许多的游玩项目，踏瀑戏水雨林拓展，高空滑索。里面也有当地美食，超市，纪念品等等。自由行的也会安排导游服务服务，各项服务很好，在三亚有多个点设有专车，一百多很超值。值得推荐体验', '2016-03-04 15:37:22', '-1940604466:http://p1.meituan.net/shaitu/d75096a8dbc057dfaf7ff4a68cabe08b94004.jpg;\n-1940087988:http://p1.meituan.net/shaitu/ca9e2ba5440943327443f6567e667f4b107096.jpg;\n-1940208457:http://p0.meituan.net/shaitu/d4cf00b067e5ed30ec181cff681d62b3121716.jpg;\n-1940208441:http://p0.meituan.net/shaitu/b6cd9e29a3d0eacd182312c7cc0ea95770876.jpg;\n-1940059256:http://p1.meituan.net/shaitu/f1d5ec5a9416077354b938b36b67710634672.jpg;\n', 'None');
