INSERT INTO `meituan_yanuoda` VALUES ('7047', 'hZM489702054', '51881475', '', '呀诺达环境非常好，工作人员非常热情，尤其是导游乔保涛接待我们非常亲切，让我们感到像是对待自己家人的感觉！非常亲切，讲解的很到位，这次在呀诺达不小心把脚崴了，乔保涛的应急处理非常快，这么个大男孩让我感到了对待工作的认真负责，对待游客的热情照顾！乔保涛给你点赞哈！', '2016-03-02 21:09:56', '-1930921767:http://p1.meituan.net/shaitu/4e91e0535f749ccfd3549819b43112ed683385.jpg;\n-1930907217:http://p0.meituan.net/shaitu/b67b3b0f5290d9995b61d3dbe6b1d3fd726260.jpg;\n', 'None');
