INSERT INTO `meituan_yanuoda` VALUES ('7077', '溪帅溪帅', '37422230', '', '服务周到，环境优美。去呀诺达热带雨林公园还是去天堂森林公园纠结了好久。最后还是去了呀喏达，明智之选。看到了传说中的见血封喉数，船屋，服务态度非常非常的好，非常满意的一次游玩！推荐这种服务优质讲信用的商家！以后会经常光顾！谢谢服务的小妹！', '2016-02-28 15:18:40', '-1987467347:http://p0.meituan.net/shaitu/b10cf3e2ca3b8894e5e8e5449d557964343157.jpg;\n-1987464759:http://p1.meituan.net/shaitu/351e296a1e3f58bbff89b4d502d185bc319774.jpg;\n-1987413184:http://p0.meituan.net/shaitu/abd8bbe6194a1556ceeb92d4435f3553243752.jpg;\n-1987464743:http://p0.meituan.net/shaitu/fa27cbc25202d9f9655116597de8489d154726.jpg;\n', 'None');
