INSERT INTO `meituan_yanuoda` VALUES ('7104', 'Rog191397817', '191548439', '', '景区里面的服务真心不错！工作人员很有礼貌，也很热情！环境设施都很棒，如想像阿凡达一样在天空翱翔，有个拓展项目叫高空滑索，以我的体重，100秒“嗖”的一下就飞过去了，感觉太棒了！', '2016-02-26 14:07:41', '', 'None');
