INSERT INTO `meituan_yanuoda` VALUES ('7110', 'yll15869682277', '45744342', '', '之前不知道美团上还带有导游的美团票，有点不相信，致电过后才知道是真的，价格便宜还包接送，还有门票，更赞的是导游很好，真的是无可挑剔啊，总之到了之后一点也不失望，美团基本上是不评价的，本人讨厌这些，不止是美团，基本上网上的我都不评，但是这次是真的很好，必须要赞一个，空下时间来评价啊', '2016-02-26 00:11:35', '', 'None');
