INSERT INTO `meituan_yanuoda` VALUES ('7306', '月下百合', '7450601', '', '环境好，树木种类繁多虽不认识但都有标牌。景区的入口很气派，服务也算热情，但景区内向游客推荐收费服务项目被拒接后态度就冷漠了，对于带小孩去玩的应该指导合理路线，节省体力又不虚此行！', '2016-02-14 22:19:39', '', 'None');
