INSERT INTO `meituan_yanuoda` VALUES ('7308', 'Mdp400256559', '311977249', '', '坐车刚到山顶发现孩子发烧，跟司机说了以后司机二话没说拉着我们下山之后换最快的车到医务室！美女医生也是给孩子倒水拿杯子跟前跟后照顾，而后小孩睡着又让我们大人二次进院参观，全程微笑服务！正能量满满的万分感激，真的像这名字一样有正能量！呀诺达！加油！', '2016-02-14 21:57:13', '-1928587795:http://p0.meituan.net/shaitu/237c069af6df468e816fb195d10fbf07882634.jpg;\n', 'None');
