INSERT INTO `meituan_yanuoda` VALUES ('7407', '刘毓颜', '12501564', '', '是个值得来的地方，空气清新，戏水项目很有趣，教练认真负责，能够确保每位队员的安全。带宝宝去的，本想试着完成闯关，但是教练处于安全考虑不让走，只好带孩子走栈道了。看着他们玩也很有趣，总值是个值得一游的地方。', '2016-02-07 00:59:39', '-1929974185:http://p1.meituan.net/shaitu/4c2fee44740535b34d0f2038ece84fab526718.jpg;\n-1930005553:http://p1.meituan.net/shaitu/c54087492e04749c4a91b776b94eaa61308102.jpg;\n-1929885888:http://p0.meituan.net/shaitu/1de5dd20233930f8a578b87f13d67afb425480.jpg;\n', 'None');
