INSERT INTO `meituan_yanuoda` VALUES ('7471', 'hankui1234', '80684224', 'https://img.meituan.net/avatar/e0ce95f2383b5e17ae0fc2bb310b780333569.jpg', '说好的电瓶车就是从大门拉到景点的必须的用车，在景点想做电瓶车还让包车，让散客租导游，80一小时，否则想逛完全部景点就很累了，景点不错，服务太一般了', '2016-01-31 19:39:25', '', 'None');
