INSERT INTO `meituan_yanuoda` VALUES ('7896', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '服务给人印象很深刻，呀诺达的集团文化很好，里面的游线分静线和动线，静线主要是雨林部分，感觉很快就走完了，时间有点仓促，还有就是植物标签上希望可以配一张图片，动线是水上项目，类似于水上拓展一样，整天而言也还不错', '2015-10-09 10:50:29', '', 'None');
