INSERT INTO `meituan_yanuoda` VALUES ('7898', 'LsS340743511', '262983387', '', '挺好的服务，挺好的景点，挺好的选择，下次来我一定会再次选择米团，选择一日游，这样的出游会更自由，更舒服，更能突出重点项目去旅行，会浪费时间在一些不值得看的景点和被购物中，这是我今次旅游的最大的收获和总结，也希望驴友们今后也不要花冤枉钱和浪费时间', '2015-10-09 10:49:00', '-2083904240:http://p1.meituan.net/shaitu/ef684cbe762c1c6093a1d0d585039bad264344.jpg;\n-2083792324:http://p0.meituan.net/shaitu/61fd70ee448ad55e3c96a7417da8a4d1469433.jpg;\n-2083869594:http://p0.meituan.net/shaitu/bbbb37f443306a9772eed42239b538b5355288.jpg;\n', 'None');
