INSERT INTO `meituan_yanuoda` VALUES ('7900', 'ABC不说话', '23696729', 'https://img.meituan.net/avatar/70e94e0ca592d2819428a01a846dcbcc197846.jpg', '很不错，景区人员很热情。这个挺划算的。就是我们住的地方去景点很远，有一百多公里。车费比较贵，150左右。但是景区人员的热情还是一个很不错体验。美团上购买的没有另外收费,景区有水路那个要另外收费，可自选。', '2015-10-08 19:21:47', '', 'None');
