INSERT INTO `meituan_yanuoda` VALUES ('7915', 'vivian718ma', '386840', 'https://img.meituan.net/avatar/f4863720ce5d0d2987cfa73da79b99fd110878.jpg', '票价偏贵，景区门票148，走水路需要另外加100元，玩CS又另外掏80，不过还蛮好玩，儿子很喜欢，景区工作人员很热情，训练有素！', '2015-10-04 08:51:46', '-1926598453:http://p1.meituan.net/shaitu/341ad8771aab0f49611a3a3d713a9768516692.jpg;\n-1926594157:http://p1.meituan.net/shaitu/841de2c3975c301a26c290032f295903515999.jpg;\n-1926579297:http://p0.meituan.net/shaitu/ffcdf1d4eb412785446ab4994d453849353197.jpg;\n', 'None');
