INSERT INTO `meituan_yanuoda` VALUES ('8028', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '跟朋友周末去的，很久了一直忘了评价，一直都听朋友推荐去呀诺达，夏天去像真是凉爽爆了，雨林的真的是天然的避暑胜地的，边走边休息，也认识了好多植物的名称，有点点小收获，几个小伙伴玩的很开心，下次有机会再去吧。', '2015-09-04 12:16:52', '', 'None');
