INSERT INTO `meituan_yanuoda` VALUES ('8099', 'yanfeng199103', '41928403', '', '一个热带雨林，很美！但是要带点防蚊虫的药，里面蚂蚁小虫子真的好多啊，建议早点去凉快一点，去太晚的话会很闷！景色真的没的说，服务人员很热情！还有建议多带水，小心中暑！呀诺达！', '2015-08-23 17:14:13', '-2077087993:http://p0.meituan.net/shaitu/3a27e7c0e9ba11cbbf0deadcb7c728b7609828.jpg;\n-2077052962:http://p0.meituan.net/shaitu/96a9e2cf6dda5c3a583f6575ea2a8d9d417628.jpg;\n-2077087977:http://p1.meituan.net/shaitu/c3378d6f5037020c212575edfe6c2916460972.jpg;\n-2077125918:http://p1.meituan.net/shaitu/9c724da690d077995f3c9709a2cf681b495590.jpg;\n', 'None');
