INSERT INTO `meituan_yanuoda` VALUES ('8187', 'sususinasinb', '1571781', '', '发展很成熟，行程安排很好，景区氛围非常不错，套票很划算，自助餐不错，景色美，厕所干净，尤其适合带小孩子来', '2015-08-04 19:38:16', '', 'None');
