INSERT INTO `meituan_yanuoda` VALUES ('8194', 'dDu244026607', '134281591', 'https://img.meituan.net/avatar/1323cc8eefa108f246a7e6f6d8cc9d6f70111.jpg', '我在美团上买的298两个人但是我们去景区取票的时候是138一个人！美团比景区价位还贵11元！打电话问也没人管！真不知道这是怎么回事！这里提醒要想便宜可以做出租车司机问价真的便宜！真的是上当受骗了', '2015-08-03 19:22:18', '', 'None');
