INSERT INTO `meituan_yanuoda` VALUES ('8299', '茹心豆子', '78096593', '', '环境特别好，大家一定要选择水路，真的非常有意思！导游不会强买强卖，而且上车下车的地方正好在我们住的酒店门口，我们住的海天大酒店，四星级，之前朋友度蜜月过来住的，经济实惠。导游虽然没有没有强买强卖，但是老是劝你买那个茶叶，说山下就没有了什么的，超市就有很便宜，喝了，味道也差不多……', '2015-06-28 21:53:05', '-1926722248:http://p0.meituan.net/shaitu/04f93555ce35ee6d82ee505979a15b7a88841.jpg;\n-1926689116:http://p1.meituan.net/shaitu/2459e22c5c24141c3e531838fd40f19786804.jpg;\n-1926935699:http://p1.meituan.net/shaitu/56cfd22946c1f0c5d3c3ca9a0828769990313.jpg;\n-1926689100:http://p0.meituan.net/shaitu/f5bc0cb9d5429f1c2603ce64db247a7894895.jpg;\n-1927295282:http://p0.meituan.net/shaitu/1c306c3331f24044846a32fa4e63278c111662.jpg;\n-1927372106:http://p0.meituan.net/shaitu/20fcb05bfe18ab9cf2d1070f5af26730109649.jpg;\n-1926948957:http://p0.meituan.net/shaitu/8f9bb2d48198a0f589d70f8a17e91b2144390.jpg;\n-1926768612:http://p0.meituan.net/shaitu/3ebb48c24777b6cbac51c627ea473d3692002.jpg;\n', 'None');
