INSERT INTO `meituan_yanuoda` VALUES ('8335', '麦格007', '9016660', 'https://img.meituan.net/avatar/50063dd78a7ca654b3505df03d12dbee39186.jpg', '风景不错，要玩梦幻谷另加了100块（只适合亲子和老年人，玩的不过瘾很小儿科），午餐58，号称中国第一索道要了76，反正都是要钱，下午的行程非常赶，雨林植物非常多，想好好看看，但导游都没怎么介绍就直接带到卖茶的地方了。。如果要去强烈推荐自驾游，不要跟团', '2015-06-17 15:12:51', '', 'None');
