INSERT INTO `meituan_yanuoda` VALUES ('8369', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '蛮不错的，态度好，导游也挺幽默', '2015-05-24 18:38:09', '', 'None');
INSERT INTO `meituan_yanuoda` VALUES ('8370', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺不错的，服务很好，导游挺好玩的', '2015-05-24 18:34:52', '', 'None');
INSERT INTO `meituan_yanuoda` VALUES ('8371', '阮行风', '134480243', '', '导游满分，踏水有点啃爹，其它都不错！消费自愿景区是统一管理没有小商贩，我买了茶叶180，大家注意，风景很好，服务很好，踏水100块钱就是走有水沟的那条路（城里人体验下可以）！来三亚看千古情演出吧！', '2015-05-24 17:13:05', '', 'None');
INSERT INTO `meituan_yanuoda` VALUES ('8372', 'dingyanqing567', '22763419', '', '不值得，就是一座南方的山，给一些南方的普通植物标注了历史，没劲', '2015-05-24 13:20:24', '', 'None');
