INSERT INTO `meituan_yanuoda` VALUES ('8377', 'wszhaojie', '130599726', 'https://img.meituan.net/avatar/59e8bc3c5be3bcad77974f7a69efc4e315953.jpg', '这是带爸爸妈妈去三亚的第一个景点，真是美极了，妈妈说，景点没有买茶叶后悔了，最感动的是呀诺达的原始味道保持的特别好，之前我们去了临春公园，和呀诺达没法比，因为人家来接的，所以不必担心路远坐车打车的问题。就是没有在那里吃饭，别人说很好吃的。', '2015-05-23 20:25:14', '-2056583621:http://p0.meituan.net/shaitu/6b4e381be8cbad374413bb3acc53b164101491.jpg;\n-2056543064:http://p0.meituan.net/shaitu/7537443acd73eaa7c8f5e769ffd7dc96109924.jpg;\n-2056492588:http://p1.meituan.net/shaitu/bfd709d4c13962d2204a241d6d37b22291766.jpg;\n-2056583605:http://p1.meituan.net/shaitu/e4c3639d25281dbd35c0a9770e915120116919.jpg;\n', 'None');
