INSERT INTO `meituan_yanuoda` VALUES ('8383', '小鱼198109', '109336940', '', '不错的选择，导游服务很到位，买了一些当地的茶叶，感觉很好，是一个值得去的地方。', '2015-05-17 14:14:43', '-1932741409:http://p1.meituan.net/shaitu/bfa108519899a1a109a01149140f0a04439188.jpg;\n-1932757927:http://p1.meituan.net/shaitu/44eda9e7f0e96651c61966cd9047766e255738.jpg;\n-1932749261:http://p0.meituan.net/shaitu/096e5123a0bdfe0d84850f9d85513df5415235.jpg;\n-1933098146:http://p0.meituan.net/shaitu/41bd7d68578e1511485191a667010bea268314.jpg;\n', 'None');
