INSERT INTO `meituan_yanuoda` VALUES ('8953', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '呀诺达的文化底蕴很棒的，不过和亚龙湾差不多，索道是必玩的，很刺激，还有就是爬山看景了，这倒没什么特别的，可能是没有去踏浪的原因吧，没有玩水很遗憾', '2015-01-04 14:05:25', '-1932182914:http://p0.meituan.net/shaitu/adf47dcbc5634dc4a6fdcf6ff54b5851107877.jpg;\n-1931611832:http://p1.meituan.net/shaitu/d8e3e0f9e4d754f9f9fb7cefe397622e111336.jpg;\n', 'None');
