INSERT INTO `meituan_yanuoda` VALUES ('8965', 'ONZ854613387', '134471528', '', '后悔玩水路,导游纯粹误导,说不玩水路就看到那些景点,其实在旱路一样能看的,白花了100块钱,耽误的好景没捞着看,因为玩完水路就天黑了,哪还有时间看山景.', '2015-01-01 15:48:12', '', 'None');
INSERT INTO `meituan_yanuoda` VALUES ('8966', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '总体感觉可以。。但作为大陆人 还是感觉海南这边的山不行！不够挺拔 威严 海拔太低了！', '2015-01-01 15:20:48', '', 'None');
