INSERT INTO `meituan_yanuoda` VALUES ('9327', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '可谓一波三折。前两天打电话预约的，服务态度好。但因接客，路况等多种原因，原订9点来公交车接，等到9点半。美团上的行程不靠谱，最早的车是9点，回来最早下午5点，但因为上午迟半个小时，所以回来就迟半小时。去的车就是回来的车。把预订的计划全打乱，原订根据美团上回来最迟下午3的，但实际不是这样的，没办法最后从景区打车回三亚湾。服务态度不错，环境也美，希望美团上写的行程能靠谱些。故在时间安排上有点遗憾。因为与网上不一样，导致行程全乱。', '2014-08-11 20:55:47', '-1941655112:http://p0.meituan.net/shaitu/adb42b446ffa975d66f901873d5b9ef0118381.jpg;\n-1941740544:http://p1.meituan.net/shaitu/580e759367b2da851c9a71bab286709288833.jpg;\n-1941838629:http://p1.meituan.net/shaitu/4e3d54d3f088e5fc26238ff3aa5cb8ac43415.jpg;\n-1941842205:http://p0.meituan.net/shaitu/6d351b42b919d874a3f980c33e9a302973538.jpg;\n-1941826671:http://p0.meituan.net/shaitu/afc8a48d688f1e422806d550cbea5dab77131.jpg;\n', 'None');
