INSERT INTO `meituan_yanuoda` VALUES ('9445', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '槟榔谷比较值得一去。。体验黎家风俗。。有表演，可以看阿哥爬槟榔树。。呀诺达一般，所谓的天然氧吧，人多的时候大口呼吸闻到的也只是汗臭味。。其实当地满大街的人在发一日游的传单，不比团购贵，讲讲价还便宜呢。。', '2014-04-16 09:04:04', '-1938579891:http://p0.meituan.net/shaitu/__40611560__5276456.jpg;\n-1938614439:http://p1.meituan.net/shaitu/__40611518__2596154.jpg;\n-1938458384:http://p0.meituan.net/shaitu/__40611646__8940726.jpg;\n', 'None');
