INSERT INTO `meituan_yanuoda` VALUES ('9476', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '呀诺达景区很大，就是路口附近指示路牌很不显眼，看电子解说器上的地图各种混乱和迷失方向，转了半天就看了三个景点。建议组团要个导游去看。天然大氧吧。槟榔谷还没建成，不过还是有点看头的，这个景区有黎族风情的表演，很不错。', '2014-04-02 13:29:28', '', 'None');
