INSERT INTO `meituan_yanuoda` VALUES ('9608', 'Bill刘小华', '28934577', '', '美团网当地服务员居然挂我电话，实在过份！呀诺达景点还不错，本来可给五星，综合给2.5星', '2014-02-08 09:59:57', '', 'None');
