INSERT INTO `meituan_yanuoda` VALUES ('9634', 'qin少连', '70542470', 'https://img.meituan.net/avatar/e57603e3bd8476c385ad8af060b2a05d330316.jpg', '导航搜索不到，很多地图软件都没有，找了半天，打呀诺达客服电话，告知搜索 大茅山庄 前行10公里就到了', '2014-01-27 08:02:10', '', 'None');
