INSERT INTO `meituan_yanuoda` VALUES ('9637', 'Mus286725626', '66634314', '', '电话预约打了30边！！准备要退票时才打通预约电话、扫兴！都没有心情玩了、因此感觉景区也是一般般.景区内的服务非常好', '2014-01-26 20:44:30', '', 'None');
INSERT INTO `meituan_yanuoda` VALUES ('9638', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '很成熟的旅游景点，线路设计合理，爬山涉水都不是非常累。天然的氧吧！值得一游！', '2014-01-26 18:57:08', '', 'None');
