INSERT INTO `tuniu_dxdt` VALUES ('47068', '何志俊', 'http://m.tuniucdn.com/fb2/t1/G4/M00/A9/B3/Cii_J1ntSRKIET7oAAB230NXKxIAACdTgFDLxsAAHb3153_w90_h90_c1_t0.jpg', '预订过程:满意;景区:一般;', '相比天涯海角，风景好的多。交通还算方便，就是间隔时间太长！总的感觉，没有什么特色，景点的人工痕迹太严重。', '2013-09-11 13:40:40', '2018-10-16 15:13:08', '20031');
