INSERT INTO `tuniu_dxdt` VALUES ('47106', 'yan_bao', 'http://m.tuniucdn.com/fb2/t1/G1/M00/D1/6F/Cii9EVc9qReIe1a1AAb-BdosfzMAAGEiQL2cvkABv4d219_w90_h90_c1_t0.jpg', '预订过程:满意;景区:满意;', '景点很美，预定过程也很方便，就是那个自助餐有点小差，全是豆腐呀，豆腐皮、冻豆腐、豆腐啥的，好不容易来个肥肠，老爸说是假的，要说，原价是78的餐的话，太坑爹啦...电瓶车也有点不值，30元，不到5分钟的车程，一直送到底，在走回来，有点小亏哈~', '2013-05-28 09:00:04', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47107', '6812032037', '', '预订过程:满意;景区:满意;', '大小洞天景点不错，风景很美！就是去取票时候有点小波折，那边取票处工作人员很不认真，传真件明明就在下面，人家义正言辞的告诉我就是没收到，害我打了半天电话沟通！', '2013-05-21 09:10:31', '2018-10-16 15:13:08', '20031');
