INSERT INTO `tuniu_dxdt` VALUES ('47194', 'ellen_xu0317', '', '预订过程:满意;景区:不满意;', '这次三亚旅行，对途牛的网上预订还是比较满意的，但是到当地的服务觉得很差，手续繁琐，要先取预订单再去取票，预订单处没人就得不得不耐心的等待，耽误我的其他景点行程，对这点很不满意，希望你们改进。', '2013-04-23 11:40:13', '2018-10-16 15:14:05', '20031');
