INSERT INTO `tuniu_dxdt` VALUES ('47197', 'zhaodan127', '', '预订过程:满意;景区:不满意;', '什么深林公园那直接是浪费钱，特别是进去的第一站兰花谷，那直接是卖假药的地方，我劝大家不要去上当受骗，至于蜈支洲，个人认为也不是很好玩，还不如大小洞天。。。', '2013-04-19 16:20:06', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47198', 'xia820430', '', '预订过程:满意;景区:满意;', '很方便，下次继续选择途牛', '2013-04-18 10:43:19', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47199', '6812068921', '', '预订过程:满意;景区:满意;', '大小洞天很舒服，很多阴凉的地方，可惜不能游泳。很美。不过只是看到小洞天，没看到大洞天(⊙o⊙)哦。买的是套票，赠送了午餐，建议入园前吃，不然入园后出来吃则不让进园了！可惜了。订票还是不错的。', '2013-04-17 07:10:00', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47200', 'wendylulidan', '', '预订过程:满意;景区:满意;', '大小洞天我们是没有跟团的，完全自助游，在途牛订了票，去景点的“预定受理处”凭证取票就好了，非常的方便！\n景点也不错，虽然相对比较小，但是也算是有山有海的地方，这个挺适合拍拍照片，闲庭漫步的。之中的景点一般般，不过对于我们第一次去的游客来讲，景点还蛮好玩的！', '2013-04-15 14:20:17', '2018-10-16 15:14:05', '20031');
