INSERT INTO `tuniu_dxdt` VALUES ('47468', '6811038668', '', '', '速度挺快的', '2012-08-27 16:12:32', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47469', '于小三', '', '', '很好 很满意 玩的开心取票方便', '2012-08-24 20:12:51', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47470', 'tuniu01182656', '', '', '途牛订票非常便利，玩的很开心', '2012-08-23 12:09:13', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47481', 'user45226', '', '', '途牛非常方便，网上操作也很简单，比到现场直接购票便宜很多，而且取票也很顺利，觉得很合适，非常满意！', '2012-08-20 14:04:40', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47482', 'beji', '', '', '', '2012-08-16 17:30:06', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47483', 'tuniu01130013', '', '', '吹着海风，别有洞天', '2012-08-16 14:01:49', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47484', '好欣', '', '', '途牛服务不错，热线很好用，能及时解决问题，换票很顺利。', '2012-08-15 14:03:48', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47485', '熊杰', '', '', '很满意', '2012-08-09 12:58:15', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47486', '6810862065', '', '行程:满意;导游:满意;交通:满意;住宿:一般;', '订票过程很简单，易于操作，取票时凭在网上预留的取票人身份证件即可取票，与现场购票存较大的优惠得利，而且有一次更改日期的权利，很人性化。本次我们一行五人就遇到这样的事了，原订大小洞天和南山一起玩掉了，后来体力实在不支，打电话给客服，给我们另换了日期，挺满意的。', '2012-09-19 18:22:17', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47487', '6810852852', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '大小洞天是我们整个行程的最后一站，这是一个海加山的自然景点，如果住在里面也不错。预定景点门票，取票都很顺利，非常方便。', '2012-09-19 18:22:16', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47488', '兔爹', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '由于此次时间太匆忙，未能旅游此景点，办理了退票手续。感谢途牛网！', '2012-09-19 18:21:22', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47489', '6810750307', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '订票过程非常简单，电话—网上支付—预订成功。大小洞天是三亚旅游开发典型代表，根据一个传说，开发出一个旅游项目，小洞天是在一个石洞上刻上三个字，大洞天是个想象，是仙人居住的地方，常人看不到。', '2012-09-19 18:20:39', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47490', '成都旅游人', '', '行程:满意;导游:一般;住宿:满意;', '取票的时候简直是太心酸了 去的时候 被告知没有我的名字 所以 我只能联系工作人员 但是最后还是没能帮我解决 我在景区那边呆了至少半个小时 才进去 而且还是按照原价进去 虽然途牛的工作人员有说退钱给我什么的 但是来来回回打电话接电话 都有十几块了 真是太不愉快了 所以提醒朋友们 如果你订了票 前一天的早上 一定要给途牛打个电话 不然有可能就把你漏单了', '2012-09-19 18:20:14', '2018-10-16 15:13:08', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47571', 'cuiyuan', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '很漂亮 。', '2012-09-19 18:19:59', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47572', 'mervin', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '预定很方便，取票很迅速，景区风景不错，值得一去！门口黄牛多！', '2012-09-19 18:19:16', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47573', '夜郎牛肉干', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '预订和取票都很好，就是自助餐太简单了，根本就没什么吃的，不值得。', '2012-09-19 18:18:11', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47574', '6810531670', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '还是挺好的 价格合适 配对的自助餐很好哈 \r\n味道也不错 不过就是觉得大小洞天本身还是一般的', '2012-09-19 18:17:58', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47575', 'baobaowb', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '景点不错 有点远 里面有个邮政 什么养生餐太坑爹了 难吃的一塌糊涂', '2012-09-19 18:16:04', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47576', 'fjlwxxzj', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '服务很好，因为预订时候出了点小问题，但很快解决，不错！下次还会继续定！', '2012-09-19 18:15:42', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47577', 'Lsisi', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '取票过程很顺利，价格也很优惠，物有所值。\r\n素食自助餐没什么东西，但是管饱，这个价格也算可以了。\r\n电瓶车接送服务很好，我是坐车到小月湾再一路玩回来，人文气息很浓，风景也不错，下了点小雨，但不影响观景。总体感觉不错。', '2012-09-19 18:15:37', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47578', 'wilytong', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '预订过程轻松，景区景色不错，是三亚不错的景点，建议大家都去看看。', '2012-09-19 18:15:30', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47579', '6810557068', '', '行程:满意;导游:满意;交通:满意;住宿:一般;', '途牛的服务很到位，去的前一个下午还有短信提醒，取票很方便。景区挺幽静的，沿着海边散散步感觉不错。', '2012-09-19 18:15:16', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47580', '太阳的光芒', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '取票过程很顺利，工作人员的服务态度也很热情，景区的风景很美，不愧为5A级景区，值得一去。', '2012-09-19 18:14:39', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47582', 'tuniu01214566', '', '行程:一般;导游:满意;交通:满意;住宿:满意;', '挺值的价格，与我们同行的游客没有网上订票，通过现场买票，价格差不少啊，看来网上订票还是不错的，免费的自助餐也还可以吧（如果特别讲究的人我觉得还是算了吧）。', '2012-09-19 18:14:12', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47583', '6810258520', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '好，比较满意，也方便这次省乐好多钱，下次还继续来预定，希望合作愉快。', '2012-09-19 18:14:05', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47584', '6810550761', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '服务很好，速度很快，不错不错不错不错不错不错不错不错不错不错不错不错不错', '2012-09-19 18:14:01', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47585', '自由之路', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '大小洞天的预定很方便，而且允许改签时间。途牛的短信提醒也很及时和贴心。取票位置也很方便，就在景区内的游客服务中心。报预定号和名字取收据，然后在团队窗口取票即可。\r\n大小洞天的景色是个人感觉此次出游感觉最好的，景区海岸线礁石险峻，海面澎湃。站在礁石上看海（这个一定要注意安全），有一种和天地为一体，海天共为一色的感觉，个人和大自然相比，实在是很渺小，漫步在海边的感觉也很惬意。景区服务做的不错，游览车节约了游人的体力。不过套票里那个自助餐确实不咋的，口味较大的朋友肯定不适应，也难吃饱，建议还是先垫垫肚子再去游玩。', '2012-09-19 18:13:55', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47586', '6810556587', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '取票很方便，价格公道。是这次三亚游的第一站，心情很好。里边只有小洞天，整个景区叫大洞天，哈哈~', '2012-09-19 18:13:14', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47587', '6810525925', '', '行程:满意;导游:满意;交通:满意;住宿:一般;', '感觉还行，没有想像中的好。里面的套餐质量有一些差，价格有一些高，建议大家最好自带食物。', '2012-09-19 18:13:11', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47588', 'caomeidudu', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '在售票处凭身份证即可进入景区，相当方便，满意，大小洞天是我们三亚游的第一站，海景风光迷人，海浪拍击礁石的声音让人身心得到放松，还看到许多热带植物，景区也很大，可以凭票免费坐电瓶车，省去行走的时间，总体感觉比天涯海角好很多', '2012-09-19 18:12:54', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47589', 'leosogou', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '到了景点，出示身份证后，马上就拿到了票了。重要提示：三亚出租车晚上6点就下班了，如果没有车接送会很麻烦，我们6点05出来，结果求了个私家车主才得以回来。三亚包车200/天，但司机主要赚你的门票和娱乐项目，所以如果你提前自己订票，司机不肯送你的。', '2012-09-19 18:12:35', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47590', 'skysmy', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '取票就是在售票处凭身份证换。包括一顿饭，电瓶车。饭是全素的，没啥好的，管饱而已', '2012-09-19 18:12:25', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47591', 'tuniu558646', 'http://images.tuniucdn.com/head/5/558597s.jpg', '行程:满意;导游:满意;交通:满意;住宿:满意;', '非常满意途牛网的预定服务,优惠幅度挺大的,下次出游我还会考虑途牛的景点门票预定的,呵呵.\r\n取票过程也很顺利,还提前收到途牛发来的通知短信息,这点也要表扬一下!', '2012-09-19 18:12:24', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47602', '牛牛吖', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '订票和取票过程非常顺利，途牛很贴心，在当天早上还发来短信提醒，里面的海景很漂亮，一家人玩得非常开心！', '2012-09-19 18:12:21', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47603', '6810516530', '', '行程:满意;导游:一般;交通:满意;住宿:一般;', '途牛很认真,每去一个景点就会提前一天打电话确认.景点不是很大,小洞天照相的游人太多,卖的大椰子很好喝,小椰子很香甜.吃午饭的地方还算大,没有出现人挤人的现象,比有些景点好多了.都是素食,管饱不管好.', '2012-09-19 18:12:11', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47604', '6810329717', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '值得一去，椰子很大，很甘甜，弯腰走走，感觉很好，走走山路拍拍照片，听听导游的讲解！', '2012-09-19 18:12:08', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47605', '6810516005', '', '行程:满意;导游:一般;交通:满意;住宿:满意;', '是套票，包括电瓶车和午餐，还算实惠，就是午餐差一点。景点风景还是相当不错的。门票价格还算可以，我问在市里可以买到100元一张的。', '2012-09-19 18:11:54', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47606', 'tuniu01063893', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '三亚比较经典的旅游景点，套票比较划算，连电子导游也是免费的（只需要交押金）。', '2012-09-19 18:11:28', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47607', '6810383281', '', '行程:满意;导游:满意;交通:一般;住宿:满意;', '预订套票非常顺利，2011年10月8日接到途牛网和海南当地旅行社的电话，均告知预订成功直接到景区取票，但是2011年10月9日到景区取票时却没有订票信息，联系海南当地旅行社的电话后，等待十多分钟取到票。景区景色不错，母亲和女儿玩得很开心。', '2012-09-19 18:10:38', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47608', '印象方寸', 'http://images.tuniucdn.com/head/7/796400s.jpg', '行程:满意;导游:满意;交通:满意;住宿:满意;', '途牛客户很热情，有问题必定帮助解决，旅途中不时有善意短信提醒，取票过程也特别的顺利，景色很不错，视野宽阔漂亮，值得一去！', '2012-09-19 18:08:44', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47609', 'tuniu0921595', '', '行程:满意;导游:满意;交通:满意;住宿:一般;', '订票取票相对方便，但是这个旅游点没有想象中的好。作为游玩有了上乘的服务，就会有不错的心情，这个是最重要的。', '2012-09-19 18:08:09', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47610', '6810317539', '', '行程:满意;导游:满意;交通:一般;住宿:满意;', '还可以还可以还可以还可以还可以还可以还可以还可以还可以还可以还可以', '2012-09-19 18:07:58', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47611', '大毛宝', 'http://images.tuniucdn.com/head/4/455203s.jpg', '行程:满意;导游:满意;交通:满意;住宿:满意;', '订票和取票过程都是超顺利的，而且门票还管一顿午餐超值啊，另外景区的美景真是使人流连忘返啊，去了绝不会失望啊！', '2012-09-19 18:07:44', '2018-10-16 15:14:05', '20031');
INSERT INTO `tuniu_dxdt` VALUES ('47622', '6810265637', '', '行程:满意;导游:满意;交通:满意;住宿:满意;', '景色不错。对门票价格满意，取票过程亦很顺利，下次会多多光顾途牛订票', '2012-09-19 18:06:43', '2018-10-16 15:14:05', '20031');
