INSERT INTO `tuniu_lht` VALUES ('33645', '酪妈', '', '预订过程:满意;景区:满意;', '网上订确实很方便快捷，客服人员会在前一天电话联系你，告知第二天取票事项。这次的蜈支洲岛没想到那么多人都通过网订，我加到前面了嘻嘻没耽误太长时间，带老人孩子出行不容易啊！', '2014-04-02 15:22:13', '2018-10-16 13:45:40', '4780');
