INSERT INTO `tuniu_lht` VALUES ('40374', '6815098680', '', '预订优惠:满意;取票便捷:满意;景区服务:满意;', '到了景区报手机号码出示身份证非常方便，但是提醒其他出游者注意租车陷阱。车是免费给用的，但是验车的时候一定注意要用手机拍照留底。我们还车的时候说车被划伤，要索赔2000块，最后拿出照片，对方无语，才做罢。\n再来说说加油，车的油表是有问题的，加油以后要把车子发动状态停大约40分钟油表才会动。我们跑了100块不到的油，加了200块油表还是不动的。最后还车的时候遇到其他还车的人，才知道大家遇到的问题都一样。\n租车是个陷阱，请大家一定注意。', '2014-04-29 10:17:36', '2018-10-16 13:45:40', '4780');
