INSERT INTO `tuniu_lht` VALUES ('40451', '6813996160', '', '预订过程:满意;景区:满意;', '在网上预订的最大好处就是不用排着长长的队等着买票，海滩真的很美，只是皮肤也晒伤了，不过岛上的水上项目真心好贵啊', '2014-04-14 15:31:54', '2018-10-16 13:45:40', '4780');
