INSERT INTO `tuniu_lht` VALUES ('40486', 'daisy0329', '', '预订过程:满意;景区:满意;', '岛上非常美丽，心旷神怡，环岛游的时候，有一部分沿海部分是不让游客下车的，那些地方是岩石海岸，海水有多层颜色，因为有些水下是珊瑚，没有破坏和污染，看上去真的很震撼，海滩沙子真的很白很细很软，导游小姐说因为是珊瑚沙，还看到了很多野菠萝，据说不好吃哈。总之，行程很愉快', '2014-04-12 13:40:01', '2018-10-16 13:44:54', '4780');
