INSERT INTO `tuniu_lht` VALUES ('40492', 'bitlouis', '', '预订过程:满意;景区:满意;', '从市区到旅游区有一段距离 最好提前定好出租车或者拼车过去 或者坐大巴过去 到了那里再换票 很方便 上船大概15分钟到岛上 岛上很干净 我五月份去的 很晒 注意涂防晒爽 空气也很湿润 岸边也很干净 不错 推荐去看看', '2014-04-10 11:11:57', '2018-10-16 13:44:54', '4780');
